///$tab Main
SET ThousandSep=',';
SET DecimalSep='.';
SET MoneyThousandSep=',';
SET MoneyDecimalSep='.';
SET MoneyFormat='₹ #,##0.00;-₹ #,##0.00';
SET TimeFormat='h:mm:ss TT';
SET DateFormat='DD/MM/YYYY';
SET TimestampFormat='DD/MM/YYYY h:mm:ss[.fff] TT';
SET FirstWeekDay=6;
SET BrokenWeeks=1;
SET ReferenceDay=0;
SET FirstMonthOfYear=1;
SET CollationLocale='en-IN';
SET CreateSearchIndexOnReload=1;
SET MonthNames='Jan;Feb;Mar;Apr;May;Jun;Jul;Aug;Sep;Oct;Nov;Dec';
SET LongMonthNames='January;February;March;April;May;June;July;August;September;October;November;December';
SET DayNames='Mon;Tue;Wed;Thu;Fri;Sat;Sun';
SET LongDayNames='Monday;Tuesday;Wednesday;Thursday;Friday;Saturday;Sunday';
SET NumericalAbbreviation='3:k;6:M;9:G;12:T;15:P;18:E;21:Z;24:Y;-3:m;-6:μ;-9:n;-12:p;-15:f;-18:a;-21:z;-24:y';

///$tab sales
Customer_SaleOrders:
LOAD
    "Row ID",
    "Order ID",
    "Order Date",
    "Ship Date",
    "Days Delayed(Reqd to ship)",
    "Ship Mode",
    "Customer ID",
    "Customer Name",
    Segment,
    Country,
    City,
    State,
    "Postal Code",
    Region,
    "Product ID",
    Category,
    "Sub-Category",
    "Product Name",
    COGS,
    "Sales per Item",
    Discount,
    Quantity,
    "Total Sales",
    Num(Profit,'0.00','.') as Profit
FROM [lib://dataconn (gain-insights_rajasekarv)/Sales (1).xlsx]
(ooxml, embedded labels, table is in);

MinMax:
Load
Min("Order Date") as startdate,
Max("Order Date") as enddate
Resident Customer_SaleOrders;

let vStart = Num(Peek('startdate','0','MinMax'));
let vEnd = Num(Peek('enddate','0','MinMax'));

BuildRange:
Load
Date($(vStart) + IterNo() - 1) as Date
AutoGenerate 1 While $(vStart) +IterNo() -1 <= $(vEnd);

Calender:
Load
    Date as "Order Date",
    Date(Date,'DD/MMM/YYYY') AS Date,
    Month(Date) as Month,
    Year(Date) as Year,
    Day(Date) as Days,
    Date(Date,'MMM-YYYY') AS MonthYears
    Resident BuildRange
    order by Date;
   
Drop table BuildRange,MinMax;
Exit Script
